var countDownDate=new Date("March 27, 2019 17:30").getTime(),countdownfunction=setInterval(function(){var e=(new Date).getTime(),t=countDownDate-e,n=Math.floor(t/864e5),o=Math.floor(t%864e5/36e5),r=Math.floor(t%36e5/6e4),a=Math.floor(t%6e4/1e3);document.getElementById("counter").innerHTML=n+" dager "+o+" timer "+r+" minutter "+a+" sekunder ",t<0&&(clearInterval(countdownfunction),document.getElementById("counter").innerHTML="MartinCrossen har starta!")},1e3);